import { createSlice } from "@reduxjs/toolkit"



const initialState ={
    points:0,
}

export const bonusSlice = createSlice({
    name: 'bonus',
    initialState,
    reducers:{
        incrementValue:(state)=>{
            state.points +=1;
        }
    }
})

export const {incrementValue} = bonusSlice.actions;

export default bonusSlice.reducer;
