import { useDispatch, useSelector } from 'react-redux';
import {incrementValue} from '../slices/TotalSlice'

function Total() {
  

  return (
    <div className="card">
      <div className="container">
        <h4>
          <b>Total Component</b>
        </h4>
        <h3>Total Salary (Amount + Bonus) : </h3>

        <button onClick={(()=>{dispatch(incrementValue())})}>Total Salary</button>
      </div>
    </div>
  );
}

export default Total;
