import { createSlice } from "@reduxjs/toolkit";


let initialState={
    tot:0
};


export const totalSlice = createSlice({
    name: 'totalSalary',
    initialState,
    reducers:{
        sumTotal: (state )=>{
            state.tot = state.amount + state.bonus
            // state.tot += 1
        }
    }

})

export const {sumTotal} = totalSlice.actions;

export default totalSlice.reducer;
